#include <stdlib.h>

//-------------------------------------------------------------------------------------------------

void * operator new(size_t size)
{
   return malloc(size);
}

//-------------------------------------------------------------------------------------------------

void operator delete(void * ptr)
{
   free(ptr);
}

//-------------------------------------------------------------------------------------------------

void* operator new(size_t size, void* ptr )
{
   return ptr;
}

//-------------------------------------------------------------------------------------------------

void * operator new[](size_t size)
{
   return malloc(size);
}

//-------------------------------------------------------------------------------------------------

void operator delete[](void * ptr)
{
   if (ptr)
   {
      free(ptr);
   }
}

//-------------------------------------------------------------------------------------------------

